  ....
  MappedStatement.Builder statementBuilder = new MappedStatement.Builder(...)
      .....
      .useCache(valueOrDefault(useCache, isSelect))
      //在这里，就将暂存在MapperBuilderAssistant中的cache对象赋值给了MappedStatement
      .cache(currentCache);
